update tb_atendimento
set    tb_atendimento.numero_guia = pla.nova_guiaPla,
       tb_atendimento.sql_update = ISNULL(atendimento.sql_update,'')+'0919-000547_2'
from tb_atendimento atendimento
inner join tb_espelho espelho with(nolock) on (atendimento.fk_espelho = espelho.id and espelho.registro_ativo = 1)
cross apply (

	select espelho as espelhoPla,
	       atendimento as atendimentoPla,
		   nova_guia as nova_guiaPla
	from dbo.[0919_000547] planilha where espelho.numero_espelho = planilha.espelho and atendimento.numero_atendimento_automatico = planilha.atendimento

) as pla

where atendimento.sql_update like '%0919-000547%' and espelho.numero_espelho = pla.espelhoPla and atendimento.numero_atendimento_automatico = pla.atendimentoPla

